import javax.servlet.*;import javax.servlet.http.*;import java.io.*;import java.util.*;public class ProcessForm extends HttpServlet{     public void doGet(HttpServletRequest request,          HttpServletResponse response) throws ServletException, IOException     {	response.setContentType("text/html");	PrintWriter out = response.getWriter();	out.print("<html>");	out.print("<head><title>Servlet ProcessForm</title>");	out.print("</head>");	out.print("<body>");	out.print("<h2>Welcome to my sevlets ");	out.print(request.getParameter("user"));	out.print("</h2>");	out.print("</body>");	out.print("</html>");     }     public void doPost(HttpServletRequest request,     	HttpServletResponse response)  throws ServletException, IOException     {     	doGet(request, response);     }}