package Steve;import javax.servlet.jsp.*;import javax.servlet.jsp.tagext.*;public class SimpleBodyTag extends javax.servlet.jsp.tagext.TagSupport{	private String level = "2";	     public int doStartTag() throws JspException     {          try          {               pageContext.getOut().print("<h" + level + "> * * ");          }          catch (Exception e)          {               throw new JspTagException(e.getMessage());          }          return EVAL_BODY_INCLUDE;     }     public int doEndTag() throws JspException     {          try          {               pageContext.getOut().print(" * * </h" + level + ">");          }          catch (Exception e)          {               throw new JspTagException(e.getMessage());          }          return EVAL_PAGE;     }          public void setLevel(String text)     {     	level = text;     }}